window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".select"),t=document.getElementById("contact-time"),c=document.getElementById("contact-price-plan"),n=document.querySelectorAll(".select__list-item");e.forEach(e=>{e.addEventListener("click",()=>{e.classList.toggle("select_active")})}),n.forEach(e=>{e.addEventListener("click",()=>{t.value=e.innerHTML})}),n.forEach(e=>{e.addEventListener("click",()=>{c.value=e.innerHTML})})});