window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".menu__list_mobile"),t=document.querySelectorAll(".menu__list-item-link"),s=document.querySelector(".menu__burger"),l=document.querySelectorAll(".form__select-wrapper");s.addEventListener("click",()=>{s.classList.toggle("active"),e.classList.toggle("active")}),t.forEach(t=>{t.addEventListener("click",()=>{s.classList.toggle("active"),e.classList.toggle("active")})}),l.forEach(e=>{e.firstElementChild.addEventListener("focus",()=>{e.classList.add("form__select-wrapper-focus")}),e.firstElementChild.addEventListener("blur",()=>{e.classList.remove("form__select-wrapper-focus")})})});