window.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".select").forEach(e=>{e.addEventListener("click",()=>e.classList.toggle("select_active"),!0),console.log(e)});document.querySelectorAll(".select__list-item").forEach(e=>{e.addEventListener("click",()=>{e.parentElement.parentElement.firstElementChild.value=e.innerHTML,e.parentElement.parentElement.parentElement.classList.remove("select_active")})})});